mapper.select:
/* 태그 개수 조회 */
select count(tag_name) count
  from 13_t_tag
 where tag_name = #{tagName}

/* 태그 조회 */
select tag_name
      ,tag_summary
      ,tag_url
  from 13_t_tag
 where tag_name = #{tagName}

mapper.insert:
/* 태그 저장 */
insert into 13_t_tag (
     tag_name
    ,tag_summary
    ,tag_url
) values (
     #{tagName}
    ,#{tagSummary}
    ,#{tagUrl}
)

mapper.update:
/* 태그 수정 */
update 13_t_tag
   set tag_summary = #{tagSummary}
      ,tag_url  = #{tagUrl}
 where tag_name = #{tagName}

mapper.delete:
/* 태그 삭제 */
delete
  from 13_t_tag
 where tag_name = #{tagName}
