mapper.select:
/* 태그 매핑 목록 조회 */
select board_id
      ,write_no
      ,tag_name
  from 13_t_tagmapping
 where board_id = #{boardId}
   and tag_name = #{tagName}
 order by ${sort} desc
 limit #{offset}, #{sizePerList}

/* 태그 매핑 개수 조회 */
select count(boardId) count
  from 13_t_tagmapping
 where board_id = #{boardId}
   and tag_name = #{tagName}

/* 태그 매핑 조회 */
select board_id
      ,write_no
      ,tag_name
  from 13_t_tagmapping
 where board_id = #{boardId}
   and write_no = #{writeNo}
   and tag_name = #{tagName}

mapper.insert:
/* 태그 매핑 저장 */
insert into 13_t_tagmapping (
     board_id
    ,write_no
    ,tag_name
) values (
     #{boardId}
    ,#{writeNo}
    ,#{tagName}
)

mapper.update:
/* 태그 매핑 수정 */
update 13_t_tagmapping
   set tag_name = #{tagName}
 where board_id = #{boardId}
   and write_no = #{writeNo}

mapper.delete:
/* 태그 매핑 삭제 */
delete
  from 13_t_tagmapping
 where board_id = #{boardId}
   and write_no = #{writeNo}
